-- Create database if it does not exist
CREATE DATABASE IF NOT EXISTS playground;

-- Create our tables
DROP TABLE IF EXISTS locations;
CREATE TABLE locations(
	id int(8) NOT NULL AUTO_INCREMENT,
    name varchar(255),
    description text,
    point POINT,
    PRIMARY KEY (id)
);

DROP TABLE IF EXISTS states;
CREATE TABLE states(
	id int(8) NOT NULL AUTO_INCREMENT,
    name varchar(255),
    poly GEOMETRY,
    PRIMARY KEY (id)
);

